package com.example.customers_list;

import java.io.*;
import java.util.ArrayList;
import java.util.List;


import model.Customers;

import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

@WebServlet(name = "customerServlet", value = "/customer-servlet")
public class CustomerServlet extends HttpServlet {
    private static List<Customers> customersList = new ArrayList<>();

    static {
        customersList.add(new Customers("Mai Văn Hoàn", "1983-08-20", "Hà Nội", "data:image/jpeg;base64,"));
        customersList.add(new Customers("Nguyễn Văn Nam", "1983-08-21", "Bắc Giang", "data:image/jpeg;base64,"));
        customersList.add(new Customers("Nguyễn Thái Hòa", "1983-08-22", "Hà Nội", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVty78zknzeYtivx9OuOd94f0l-_ifWoM0wg&usqp=CAU"));
        customersList.add(new Customers("Trần Đăng Khoa", "1983-08-17", "Hà Tây", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRnkAkDkyXCzErdPKvQbrwYySzZ_k9I4f1HfQ&usqp=CAU"));
        customersList.add(new Customers("Nguyễn Đình Thi", "1983-08-19", "Hà Nội", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRrfO2lTsAWOZHoZ_IR1c1x6M7l8DdkaiP6QB_tiF8bszN0tyE47TvNi7YvEwz6AD2ADes&usqp=CAU"));
    }

    public void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException, ServletException {
        request.setAttribute("customersList", customersList);
        request.getRequestDispatcher("/index.jsp").forward(request, response);
    }

}